import { Component, OnInit } from '@angular/core';
import { Livre } from '../model/Livre.model';
import { LivreService } from '../services/Livre.service';
import { AuthService } from '../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-livres',
  templateUrl: './livres.component.html'
})
export class LivresComponent implements OnInit {

  livres!: Livre[];

  constructor(
    private livreService: LivreService,
    public authService: AuthService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.chargerLivres();
  }

  chargerLivres() {
    this.livreService.listeLivres().subscribe(livres => {
      console.log(livres);
      this.livres = livres;
    }); 
  }

  supprimerLivre(livre: Livre) {
    let conf = confirm("Etes-vous sûr ?");
    if (conf) {
      this.livreService.supprimerLivre(livre.idLivre).subscribe(() => {
        console.log("Livre supprimé");
        this.chargerLivres();
      });
    }
  }
}
<html>
    <div class="container">
        <div class="card shadow mb-4">
            <div class="card-body">
                <table class="table table-striped">
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Nom</th>
                            <th>Prix</th>
                            <th>Date Création</th>
                            <th>Auteur</th>
                        </tr>
                    </thead>
                    <tbody *ngFor="let element of livres">
                        <tr>
                            <td>{{ element.idLivre }}</td>
                            <td>{{ element.nomLivre }}</td>
                            <td>{{ element.prixLivre }}</td>
                            <td>{{ element.dateCreation | date:'dd/MM/yyyy' }}</td>
                            <td>{{ element.auteur.nomAuteur }}</td>
                            <td><a *ngIf="authService.isAdmin()" class="btn btn-danger" (click)="supprimerLivre(element)">Supprimer</a></td>
                            <td><a *ngIf="authService.isAdmin()" class="btn btn-success" [routerLink]="['/update-livre', element.idLivre]">Modifier</a></td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</html>
