import { Component, OnInit } from '@angular/core';
import { Livre } from '../model/Livre.model'; // Changement d'importation
import { ActivatedRoute, Router } from '@angular/router';
import { LivreService } from '../services/Livre.service'; // Changement d'importation
import { Auteur } from "../model/auteur.model";

@Component({
  selector: 'app-update-livre', // Changement de sélecteur
  templateUrl: './update-livre.component.html' // Changement de templateUrl
})
export class UpdateLivreComponent implements OnInit { 

  currentLivre = new Livre(); 
  auteurs!: Auteur[]; 
  updatedAuteurId!: number;

  constructor(
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private livreService: LivreService // Changement de service
  ) { }

  ngOnInit(): void {
    this.livreService.listerAuteurs().subscribe(auteurs => {
      this.auteurs = auteurs;
      console.log(auteurs);
    });
    this.livreService.consulterLivre(this.activatedRoute.snapshot.params['id']).subscribe(livre => {
      this.currentLivre = livre;
      this.updatedAuteurId = this.currentLivre.auteur.idAuteur;
    });
  }

  updateLivre() { 
    this.currentLivre.auteur = this.auteurs.find(auteur => auteur.idAuteur == this.updatedAuteurId)!;
    this.livreService.updateLivre(this.currentLivre).subscribe(livre => {
      this.router.navigate(['livres']);
    });
  }
}
<div class="container">
    <div>
        <h2>Modifier un Livre :</h2>
    </div>
    <form (ngSubmit)="updateLivre()">
        <div class="col-sm-2 col-md-2 col-lg-2">

            <input readonly type="text" [(ngModel)]="currentLivre.idLivre" name="idLivre"
                class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Nom Livre</label>
            <input type="text" [(ngModel)]="currentLivre.nomLivre" name="nomLivre" class="form-control">
        </div>
        <div class="col-sm-2 col-md-2 col-lg-2">
            <label>Prix Livre</label>
            <input type="number" [(ngModel)]="currentLivre.prixLivre" name="prixLivre"
                class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Date de création</label>
            <input type="date" [ngModel]="currentLivre.dateCreation | date: 'yyyy-MM-dd'"
                (ngModelChange)="currentLivre.dateCreation = $event" name="dateCreation" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Auteur</label>
            <select class="form-control form-control-lg" id="idAuteur" name="idAuteur" [(ngModel)]="updatedAuteurId">
                <option *ngFor="let auteur of auteurs" [value]="auteur.idAuteur">
                    {{auteur.nomAuteur}}
                </option>
            </select>
        </div>
        <div>
            <button type="submit" class="btn btn-success">Modifier</button>
        </div>
    </form>
</div>
