import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Livre } from '../model/Livre.model';
import { LivreService } from '../services/Livre.service';
import { Auteur } from "../model/auteur.model";

@Component({
  selector: 'app-add-livre',
  templateUrl: './add-livre.component.html',
  styles: []
})
export class AddLivreComponent implements OnInit {

  livre = new Livre();
  Auteurs!: Auteur[];
  newIdAuteur!: number;
  newAuteur!: Auteur;

  constructor(
    private router: Router,
    private livreService: LivreService
  ) {}

  ngOnInit(): void {
    this.livreService.listerAuteurs().subscribe(auteurs => {
      this.Auteurs = auteurs;
      console.log(auteurs);
    });
  }

  addLivre() {
    this.livre.auteur = this.Auteurs.find(auteur => auteur.idAuteur == this.newIdAuteur)!;
    this.livreService.ajouterLivre(this.livre).subscribe(livre => {
      console.log(livre);
      this.router.navigate(['livres']);
    });
  }
}
<div class="container">
    <div>
        <h2>Ajouter un produit livre :</h2>
    </div>
    <form>
        <div hidden class="col-sm-2 col-md-2 col-lg-2">
            <label>ID livre</label>
            <input type="text" [(ngModel)]="livre.idLivre" name="idLivre" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Nom livre</label>
            <input type="text" [(ngModel)]="livre.nomLivre" name="nomLivre" class="form-control">
        </div>
        <div class="col-sm-2 col-md-2 col-lg-2">
            <label>Prix livre</label>
            <input type="number" [(ngModel)]="livre.prixLivre" name="prixLivre" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Date création</label>
            <input type="date" [(ngModel)]="livre.dateCreation" name="dateCreation" class="form-control">
        </div>
        <div class="col-sm-4 col-md-4 col-lg-4">
            <label>Auteur</label>
            <select class="form-control form-control-lg" id="idAuteur" name="idAuteur" [(ngModel)]="newIdAuteur">
                <option *ngFor="let auteur of Auteurs" [value]="auteur.idAuteur">
                    {{ auteur.nomAuteur }}
                </option>
            </select>
        </div>

        <div class="mt-2">
            <button type="submit" (click)="addLivre()" class="btn btn-success">Ajouter</button>
        </div>
    </form>
</div>
