import { LivreService } from '../services/Livre.service';
import { Component, OnInit } from '@angular/core';
import { Livre } from '../model/Livre.model';
import { Auteur } from '../model/auteur.model';

@Component({
  selector: 'app-recherche-par-auteur',
  templateUrl: './recherche-par-auteur.component.html',
})
export class RechercheParAuteurComponent implements OnInit {
  livres: Livre[] = [];
  IdAuteur: number | undefined;
  auteurs: Auteur[] = [];
  NomAuteur: string = '';

  constructor(private livreService: LivreService) { }

  ngOnInit(): void {
    this.livreService.listerAuteurs().subscribe(response => {
      this.auteurs = response;
    });
  }

  onChange(): void {
    if (this.IdAuteur !== undefined && !isNaN(this.IdAuteur)) {
      this.livreService.livresParAuteur(this.IdAuteur)
        .subscribe(livres => {
          this.livres = livres;
        });
    } else {
      console.error('IdAuteur is not a valid number. Cannot make the request.');
    }
  }
}
<html>
    <div class="container">
        <div class="col-sm-4 col-md-4 col-lg-4 mb-4">
            <label>Auteur</label>
            <select class="form-control form-control-lg" id="idAuteur" name="idAuteur" [(ngModel)]="IdAuteur"
                (change)="onChange()">
                <option *ngFor="let auteur of auteurs" value="{{auteur.idAuteur}}">
                    {{auteur.nomAuteur}}
                </option>
            </select>
        </div>
        <div class="card shadow mb-4">
            <div class="card-body">
                <table class="table table-striped">
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Nom</th>
                            <th>Prix</th>
                            <th>Date Création</th>
                            <th>Auteur</th>
                        </tr>
                    </thead>
                    <tbody *ngFor="let element of livres">
                        <tr>
                            <td>{{element.idLivre}}</td>
                            <td>{{element.nomLivre}}</td>
                            <td>{{element.prixLivre}}</td>
                            <td>{{element.dateCreation | date:'dd/MM/yyyy'}}</td>
                            <td>{{element.auteur.nomAuteur}}</td>
                            <!-- <td><a *ngIf= "authService.isAdmin()" class="btn btn-danger" (click)="supprimerLivre(element)">Supprimer</a></td>
                            <td><a *ngIf= "authService.isAdmin()" class="btn btn-success" [routerLink]="['/update-livre', element.idLivre]">Modifier</a></td> -->
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</html>
